#compte le nombre de clefs dans "Journalist"
def howmany_question(journalist)
	x=0
	while journalist[x]
	x += 1
	end
	puts x

end

def numbers_search(journalist)
	n=0
	nb=0
	while journalist[n]
		if journalist[n] =~ /[0-9]/
		n += 1
		nb += 1

		else
		n += 1
		end
	end
	puts nb

end

#Recherche des lettres de "aude"
def letter_search(journalist)
	n=0
	nb=0
	while journalist[n]
		if journalist[n].downcase.include? "aude"
		nb += 1
		end
	n += 1
	end
	puts nb
end

#Combien commence par une majuscule
def howmany_upper_case(journalist)
	n=0
	nb=0
	nc=1
	while journalist[n]
		if journalist[n][nc] =~ /[A-Z]/
		nb+=1
		end
	n+=1
	end
	puts nb
end
#Combien ont des majuscule
def numbers_of_upC(journalist)
	n=0
	nb=0
	while journalist[n]
		if journalist[n] =~ /[A-Z]/
		n += 1
		nb += 1

		else
		n += 1
		end
	end
	puts nb
end
#nombre de dash
def numbers_of_dash(journalist)
		n=0
		nb=0
	while journalist[n]
			if journalist[n] =~ /_/
			n += 1
			nb += 1

			else
			n += 1
			end
	end
		puts nb

end

#ordre alphabetique
def alphabetical(journalist)
	puts journalist.sort
end
#Menu
def menu_q(journalist)
	part = ["1- Combien y a t'il de journalistes dans le tableau ?","2- Combien d'adresse contiennent un numéro ?","3- Combien d'adresse contiennent les 4 lettres du prénom 'Aude' à la suite (sans faire attention à la casse) ?","4- Combien commencent par une majuscule ?","5- Combien contiennent une majuscule ?","6- Combien de _ dans tous les pseudos confondus ?","7- Trie la liste de handle par ordre alphabétique"]
	puts '-'*73+"\n"+"-"*1+" Voici la liste des twittos/journalistes orbitants autour de la chine. "+"-"*1+"\n"+'-'*24+" Que voulez vous faire ? "+'-'*24+"\n"+'-'*73
	puts part << "-"*45+"\nChoisissez entre 1 et 7\n"+"-"*45
	answer1=gets.chomp.to_i

	if answer1 > 0 && answer1 < 8
		 		if answer1 == 1
					howmany_question(journalist)
				elsif answer1 == 2
					numbers_search(journalist)
				elsif answer1 == 3
					letter_search(journalist)
				elsif answer1 == 4
					howmany_upper_case(journalist)
			  elsif answer1 == 5
					numbers_of_upC(journalist)
				elsif answer1 == 6
					numbers_of_dash(journalist)
				else answer1 == 7
					alphabetical(journalist)

			  end
	else answer1 != /[1-7]/
		puts "Vous devez choisir un chiffre entre 1 et 7"
		sleep 1

  end

end


menu_q(journalist)
